(function () {
    'use strict';

    var expect;
    if(typeof window === 'object' && window.expect) {
        expect = window.expect;
    }
    else {
        expect = require('chai').expect;
    }

    describe('Protolib.object.min', function () {
        before(function () {
            if(typeof window !== 'object' && !Object._) new (require('../'))('_');
        });

        it('It should return the maximum value in an object or array', function () {
            expect([1, 4, 7, 5, 99, 1, 2]._.min()).to.equal(1);
            expect(['a', 'e', 'i', 'q', 'b', 'z']._.min()).to.equal('a');
            expect([1, 'a', 4, 'r', 999]._.min()).to.equal(1);
            expect({ a: 43, b: 123, c: 0 }._.min()).to.equal(0);

            var data = [
                {
                    name: 'foo',
                    value: 1
                },
                {
                    name: 'bar',
                    value: 2
                },
                {
                    name: 'baz',
                    value: 3
                }
            ];

            var max = data._.min(function (item) {
                return item.value;
            });

            expect(max).to.eql({ name: 'foo', value: 1 });
        });

        it('It should simply return the object if not an array or object', function () {
            expect((5)._.min()).to.equal(5);
            expect(('string')._.min()).to.equal('string');
            expect((true)._.min()).to.equal(true);
        });
    });
}());
