(function () {
    'use strict';

    var expect;
    if(typeof window === 'object' && window.expect) {
        expect = window.expect;
    }
    else {
        expect = require('chai').expect;
    }

    describe('Protolib.object.min', function () {
        before(function () {
            if(typeof window !== 'object' && !Object._) new (require('../'))('_');
        });

        it('It should return the maximum value in an object or array', function () {
            expect([1, 4, 7, 5, 99, 1, 2]._.min()).to.equal(1);
            expect(['a', 'e', 'i', 'q', 'b', 'z']._.min()).to.equal('a');
            expect([1, 'a', 4, 'r', 999]._.min()).to.equal(1);
            expect({ a: 43, b: 123, c: 0 }._.min()).to.equal(0);

            var data = [
                {
                    name: 'foo',
                    value: 1
                },
                {
                    name: 'bar',
                    value: 2
                },
                {
                    name: 'baz',
                    value: 3
                }
            ];

            var min = data._.min(function (item) {
                return item.value;
            });

            expect(min).to.eql({ name: 'foo', value: 1 });

            data = [
                {
                    name: 'foo',
                    value: 2
                },
                {
                    name: 'bar',
                    value: 3
                },
                {
                    name: 'baz',
                    value: 1
                }
            ];

            min = data._.min(function (item) {
                return item.value;
            });

            expect(min).to.eql({ name: 'baz', value: 1 });

            data = [
                {
                    name: 'foo',
                    value: 'a'
                },
                {
                    name: 'bar',
                    value: 'd'
                },
                {
                    name: 'baz',
                    value: 'x'
                },
                {
                    name: 'hello',
                    value: 'z'
                },
                {
                    name: 'world',
                    value: 'b'
                }
            ];

            min = data._.min(function (item) {
                return item.value;
            });

            expect(min).to.eql({ name: 'foo', value: 'a' });

            data = [
                {
                    name: 'foo',
                    value: [1, 2, 3, 4]
                },
                {
                    name: 'bar',
                    value: [1, 2, 3, 4]
                },
                {
                    name: 'baz',
                    value: [1, 2, 3, 4]
                },
                {
                    name: 'hello',
                    value: [1, 2, 3, 4]
                },
                {
                    name: 'world',
                    value: [1, 2, 3, 4]
                }
            ];

            min = data._.min(function (item) {
                return item._.min();
            });

            expect(min).to.eql({ name: 'world', value: [1, 2, 3, 4] });

            data = [
                {
                    name: 'foo',
                    value: [1, 2, 3, 4]
                },
                {
                    name: 'bar',
                    value: [5, 6, 7, 8]
                },
                {
                    name: 'baz',
                    value: [9, 10, 11, 12]
                },
                {
                    name: 'hello',
                    value: [13, 14, 15, 16]
                },
                {
                    name: 'world',
                    value: [17, 18, 19, 20]
                }
            ];

            min = data._.min(function (item) {
                return item._.min();
            });

            expect(min).to.eql({ name: 'foo', value: [1, 2, 3, 4] });

            data = [
                {
                    name: 'foo',
                    value: 'a'
                }
            ];

            min = data._.min(function (item) {
                return item.value;
            });

            expect(min).to.eql({ name: 'foo', value: 'a' });

            data = [];

            min = data._.min(function (item) {
                return item.value;
            });

            expect(min).to.eql(undefined);
        });

        it('It should simply return the object if not an array or object', function () {
            expect((5)._.min()).to.equal(5);
            expect(('string')._.min()).to.equal('string');
            expect((true)._.min()).to.equal(true);
        });
    });
}());
